package program1;

public class q8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x,y;
		x=1;
		y=2;
		System.out.println("Before swapping:");
		System.out.println("x= "+x);
		System.out.println("y= "+y);
		x=x+y;
		y=x-y;
		x=x-y;
		System.out.println("After swapping:");
		System.out.println("x= "+x);
		System.out.println("y= "+y);
	}

}
