package pattern;

public class pattern_midsem {

	public static void main(String[] args) {
			// TODO Auto-generated method stub
		int t=97;
			for(int i=69;i>=65;i--) {
				for(int j=65;j<=i;j++) {
					System.out.print((char)(t)+" ");
					t++;
				}
			System.out.println();
		}
		}
	}


